import { Component, OnInit } from '@angular/core';

import { Article } from '../model/article';

@Component({
  selector: 'app-article-item',
  templateUrl: './article-item.component.html',
  styleUrls: ['./article-item.component.css']
})
export class ArticleItemComponent implements OnInit {

  public unArticulo!: Article;
  public otroArticulo!: Article;

  // constructor(){ 
  //   this.unArticulo=new Article("Iphone","../../assets/iphone.png",500,1);
  // }

  constructor() {}

  ngOnInit(){
    this.unArticulo = new Article("Iphone","../../assets/iphone.png",500,1,false);
    this.otroArticulo = new Article("Iphone2","../../assets/iphone.png",700,2,true);

  }

  incrementarUnidades(){
    this.unArticulo.añadirUnidadesEnCarrito();

  }

  decrementarUnidades(){
    this.unArticulo.decrementarUnidadesEnCarrito();


  }


}
